package medium;


//https://leetcode.com/problems/number-of-islands/submissions/
public class NumberOfIslands {

    private boolean[][] visited;
    private int[] x = new int[]{-1, 0, 1, 0};
    private int[] y = new int[]{0, 1, 0, -1};

    public int numIslands(char[][] grid) {
        visited = new boolean[grid.length][grid[0].length];
        int placer = 1;

        int[][] intGrid = new int[grid.length][grid[0].length];

        // TODO do not assign to int[][]
        for (int i = 0; i < intGrid.length; i++) {
            for (int j = 0; j < intGrid[i].length; j++) {
                intGrid[i][j] = Character.getNumericValue(grid[i][j]);
            }
        }
        for (int r = 0; r < intGrid.length; r++) {
            for (int c = 0; c < intGrid[r].length; c++) {
                if (intGrid[r][c] != 0 && visited[r][c] == false) {
                    visited[r][c] = true;
                    intGrid[r][c] = placer;
                    visitAround(intGrid, r, c, placer);
                    placer++;
                }
            }
        }
        return placer - 1;
    }

    public void visitAround(int[][] grid, int r, int c, int placer) {
        for (int i = 0; i < 4; i++) {
            int row = x[i] + r;
            int col = y[i] + c;
            if (row < 0 || row >= grid.length) continue;
            if (col < 0 || col >= grid[row].length) continue;
            if (visited[row][col]) continue;
            if (grid[row][col] != 0) {
                grid[row][col] = placer;
                visited[row][col] = true;
                visitAround(grid, row, col, placer);
            }
        }
    }
}

